/**
 * trigger name: ChatOpsEvent
 * call source: other Pulse Actions via emitEvent()
 * payload example:
 *   {
 *     "message": "insert message contents here"
 *   }
 * constants example:
 *  ChatOpsWebhook: 84d46c6a-d39d-11e9-bb65-2a2ae2dbcce4
 * outputs:
 * - the "message" object in the payload will be sent to a configured Microsoft Teams webhook
 * prerequisites: configured webhook connector for Microsoft Teams
 * external documentation: https://docs.microsoft.com/en-us/microsoftteams/platform/concepts/connectors/connectors-using
 */

exports.handler = function ({ event: body, constants, triggers }, context, callback) {
    var str = body;
    //console.log(str.message);

    var request = require('request');
    var teams_webhook = constants.TeamsWebHook;

    //console.log('About to request MS Teams webhook: ', teams_webhook);

    request({
        uri: teams_webhook,
        method: 'POST',
        json: { "text": str.message }
    }, function (error, response, body) { }
    );
}

const request = require('request');
const { Webhooks } = require('@qasymphony/pulse-sdk');
const ScenarioSdk = require('@qasymphony/scenario-sdk');

const Features = {
    getIssueLinkByFeatureName(qtestToken, scenarioProjectId, name) {
        return new ScenarioSdk.Features({ qtestToken, scenarioProjectId }).getFeatures(`"${name}"`);
    }
};

exports.handler = function ({ event: body, constants, triggers }, context, callback) {
    function emitEvent(name, payload) {
        let t = triggers.find(t => t.name === name);
        return t && new Webhooks().invoke(t, payload);
    }

    // Specific to pulse actions
    var payload = body;

    var testLogs = payload.logs;
    var cycleId = payload.testcycle;
    var projectId = payload.projectId;
    var requiresDecode = payload.requiresDecode;

    if(requiresDecode == 'true') {
        testLogs = JSON.parse(testLogs);
    }

    var scenarioCount = 0;
    var scenarioList = "";

    var standardHeaders = {
        'Content-Type': 'application/json',
        'Authorization': `bearer ${constants.QTEST_TOKEN}`
    }

    var createLogsAndTCs = function () {
        var opts = {
            url: "http://" + constants.ManagerURL + "/api/v3/projects/" + projectId + "/auto-test-logs?type=automation",
            json: true,
            headers: standardHeaders,
            body: {
                test_cycle: cycleId,
                test_logs: testLogs
            }
        };

        return request.post(opts, function (err, response, resbody) {

            if (err) {
                Promise.reject(err);
            }
            else {
                emitEvent('ChatOpsEvent', { message: resbody });

                if (response.body.type == "AUTOMATION_TEST_LOG") {
                    Promise.resolve("Uploaded results successfully");
                }
                else {
                    emitEvent('ChatOpsEvent', { message: "Wrong type" });
                    Promise.reject("Unable to upload test results");
                }
            }
        });
    };

    createLogsAndTCs()
        .on('response', function () {
            console.log("About to call Link Requirements Rule")
            emitEvent('<INSERT NAME OF LINK SCENARIO REQUIREMENTS RULE HERE>', payload);
        })
        .on('error', function (err) {
            emitEvent('ChatOpsEvent', { message: err });
        })
}

/**
 * call source: delivery script from CI Tool (Jenkins, Bamboo, TeamCity, CircleCI, etc), Launch, locally executed
 *              see 'delivery' subdirectory in this repository
 * payload example:
 * {
 *   properties: 'example value'
 *   arrayOfItems: [ { <properties and example values> } ]
 * }
 * constants:
 * - SCENARIO_PROJECT_ID: 84d46c6a-d39d-11e9-bb65-2a2ae2dbcce4
 * - QTEST_TOKEN: 84d46c6a-d39d-11e9-bb65-2a2ae2dbcce4
 * outputs:
 * - The unformatted items in the payload will be formatted into qTest test case
 * - The test cases then will be added to qTest project
 * - The unformatted result will be sent to the trigger "TriggerName"
 * - The ChatOps channel (if there is any) will notificate the result or error
 */

const { Webhooks } = require('@qasymphony/pulse-sdk');

exports.handler = function ({ event: body, constants, triggers }, context, callback) {
    function emitEvent(name, payload) {
        let t = triggers.find(t => t.name === name);
        return t && new Webhooks().invoke(t, payload);
    }

    // Payload to be passed in: json style cucumber for java test results

    /////// Pulse version
    var payload = body;
    var projectId = payload.projectId;
    var cycleId = payload.testcycle;

    let testResults = JSON.parse(Buffer.from(payload.result, 'base64').toString('ascii'));

    var testLogs = [];
    //console.log("TEST RESULTS: " + testResults);

    //emitEvent('ChatOpsEvent', { TESTRESULTS: testResults });

    testResults.forEach(function (feature) {
        var featureName = feature.name;
        feature.elements.forEach(function (testCase) {

            if (!testCase.name)
                testCase.name = "Unnamed";

            TCStatus = "passed";

            var reportingLog = {
                exe_start_date: new Date(), // TODO These could be passed in
                exe_end_date: new Date(),
                module_names: [
                    featureName
                ],
                name: testCase.name,
                automation_content: feature.uri + "#" + testCase.name
            };

            var testStepLogs = [];
            order = 0;
            stepNames = [];
            attachments = [];

            testCase.steps.forEach(function (step) {
                stepNames.push(step.name);

                var status = step.result.status;
                var actual = step.name;

                if (TCStatus == "passed" && status == "skipped") {
                    TCStatus = "skipped";
                }
                if (status == "failed") {
                    TCStatus = "failed";
                    actual = step.result.error_message;
                }
                if (status == "undefined") {
                    TCStatus = "incomplete";
                    status = "incomplete";                    
                    emitEvent('ChatOpsEvent', { message: "Step result not found: " + step.name + "; marking as incomplete." });
                }

                // Are there an attachment for this step?
                if ("embeddings" in step) {
                    console.log("Has attachment");

                    attCount = 0;
                    step.embeddings.forEach(function (att) {
                        attCount++;
                        var attachment = {
                            name: step.name + " Attachment " + attCount,
                            "content_type": att.mime_type,
                            data: att.data
                        };
                        console.log("Attachment: " + attachment.name)

                        attachments.push(attachment);
                    });
                }

                var expected = step.keyword + " " + step.name;

                if ("location" in step.match) {
                    expected = step.match.location;
                }

                var stepLog = {
                    order: order,
                    description: step.keyword + ' ' + step.name,
                    expected_result: step.name,
                    actual_result: actual,
                    status: status
                };

                testStepLogs.push(stepLog);
                order++;
            });

            reportingLog.attachments = attachments;
            reportingLog.description = stepNames.join("<br/>");
            reportingLog.status = TCStatus;
            reportingLog.test_step_logs = testStepLogs;
            reportingLog.featureName = featureName;
            testLogs.push(reportingLog);
        });
    });

    var formattedResults = {
        "projectId": projectId,
        "testcycle": cycleId,
        "logs": testLogs
    };

    emitEvent('UpdateQTestWithFormattedResults', formattedResults);

}

const PulseSdk = require('@qasymphony/pulse-sdk');
const request = require('request');
const xml2js = require('xml2js');
const { Webhooks } = require('@qasymphony/pulse-sdk');

exports.handler = function ({ event: body, constants, triggers }, context, callback) {
    function emitEvent(name, payload) {
        let t = triggers.find(t => t.name === name);
        return t && new Webhooks().invoke(t, payload);
    }

        var payload = body;
        var projectId = payload.projectId;
        var cycleId = payload.testcycle;
        var testLogs = [];

        let testResults = Buffer.from(payload.result, 'base64').toString('ascii');

        //console.log(testResults);

        var parseString = require('xml2js').parseString;
        var startTime = '';
        var endTime = '';
        var lastEndTime = 0;

        parseString(testResults, {
            preserveChildrenOrder: true,
            explicitArray: false,
            explicitChildren: false,
            emptyTag: "..."
        }, function (err, result) {
            if (err) {
                emitEvent('ChatOpsEvent', { Error: "Unexpected Error Parsing XML Document: " + err }); 
                console.log(err);
            } else {              
                if (result.testsuites) {                    
                    if (result.testsuites.testsuite) {
                        var testsuites = Array.isArray(result.testsuites['testsuite']) ? result.testsuites['testsuite'] : [result.testsuites['testsuite']];
                        testsuites.forEach(function(testsuite) {
                            lastEndTime = 0;
                            suiteName = testsuite.$.name;
                            console.log('Suite Name: ' + suiteName)
                            if (testsuite.testcase) {
                                var testcases = Array.isArray(testsuite.testcase) ? testsuite.testcase : [testsuite.testcase];
                                testcases.forEach(function(testcase) {
                                    var classArray = [];
                                    classArray = testcase.$.name.replace('=>', ':').split(':');
                                    var depth = classArray.length;
                                    var className = classArray[(depth - 1)];
                                    var moduleNames = [];
                                    var moduleCount = 0;
                                    classArray.forEach(function(folder) {
                                        if(moduleCount < (depth - 1)) {
                                            moduleNames.push(folder.trim());
                                            moduleCount++;
                                        }
                                    })
                                    if (moduleNames.length == 0) {
                                        moduleNames.push(suiteName);
                                    }
                                    console.log('Case Name: ' + className)
                                    var classStatus = 'passed';
                                    if (lastEndTime == 0) {
                                        startTime = new Date(Date.parse(testsuite.$.timestamp)).toISOString();
                                    } else {
                                        startTime = lastEndTime;
                                    }
                                    interim = new Date(Date.parse(startTime)).getSeconds() + parseFloat(testcase.$.time);
                                    endTime = new Date(Date.parse(startTime)).setSeconds(interim);
                                    endTime = new Date(endTime).toISOString();

                                    var note = '';
                                    var stack = '';
                                    var testFailure = Array.isArray(testcase.failure) ? testcase.failure : [testcase.failure];
                                    testFailure.forEach(function(failure) {
                                        if (failure) {
                                            console.log(failure.$.type)
                                            note = failure.$.type + ': ' + failure.$.message;
                                            console.log(failure._)
                                            stack = failure._;
                                            classStatus = 'failed';
                                        }
                                    });

                                    var testError = Array.isArray(testcase.error) ? testcase.error : [testcase.error];
                                    testError.forEach(function(error) {
                                        if (error) {
                                            console.log(error.$.message)
                                            note = error.$.message;
                                            classStatus = 'failed';
                                        }
                                    });

                                    var testSkipped = Array.isArray(testcase.skipped) ? testcase.skipped : [testcase.skipped];
                                    testSkipped.forEach(function(skipped) {
                                        if (skipped) {
                                            classStatus = 'skipped';
                                        }
                                    });

                                    console.log(classStatus);

                                    var testLog = {
                                        status: classStatus,
                                        name: className,
                                        attachments: [],
                                        note: note,
                                        exe_start_date: startTime,
                                        exe_end_date: endTime,
                                        automation_content: htmlEntities(className),
                                        module_names: moduleNames
                                    };

                                    if (stack !== '') {
                                        testLog.attachments.push({
                                            name: `${className}.txt`,
                                            data: Buffer.from(stack).toString("base64"),
                                            content_type: "text/plain"
                                        });
                                    }
                                    //testLog.attachments.push(payload.consoleOutput[0]);
                                    testLogs.push(testLog);
                                    lastEndTime = endTime;
                                });
                            } else {                                
                                console.log('Test Suite has no Test Cases, skipping.');
                            }
                        });
                    } else {
                        console.log('Test Suites collection is empty, skipping.');
                    }
                } else {
                    console.log('Test Suites collection doesn\'t exist, checking for singular test suite.');
                    var testsuite = result.testsuite;

                    lastEndTime = 0;
                    suiteName = testsuite.$.name;
                    console.log('Suite Name: ' + suiteName);
                    if (testsuite.testcase) {
                        var testcases = Array.isArray(testsuite.testcase) ? testsuite.testcase : [testsuite.testcase];
                        testcases.forEach(function(testcase) {
                            var classArray = [];
                            classArray = testcase.$.name.replace('=>', ':').split(':');
                            var depth = classArray.length;
                            var className = classArray[(depth - 1)];
                            var moduleNames = [];
                            var moduleCount = 0;
                            classArray.forEach(function(folder) {
                                if(moduleCount < (depth - 1)) {
                                    moduleNames.push(folder.trim());
                                    moduleCount++;
                                }
                            })
                            if(moduleNames.length == 0) {
                                moduleNames.push(suiteName);
                            }
                            console.log('Case Name: ' + className)
                            var classStatus = 'passed';
                            if(lastEndTime == 0) {
                                startTime = new Date(Date.parse(testsuite.$.timestamp)).toISOString();
                            } else {
                                startTime = lastEndTime;
                            }
                            interim = new Date(Date.parse(startTime)).getSeconds() + parseFloat(testcase.$.time);
                            endTime = new Date(Date.parse(startTime)).setSeconds(interim);
                            endTime = new Date(endTime).toISOString();

                            var note = '';
                            var stack = '';

                            var testFailure = Array.isArray(testcase.failure) ? testcase.failure : [testcase.failure];
                            testFailure.forEach(function(failure) {
                                if (failure) {
                                    console.log(failure.$.type)
                                    note = failure.$.type + ': ' + failure.$.message;
                                    console.log(failure._)
                                    stack = failure._;
                                    classStatus = 'failed';
                                }
                            });

                            var testError = Array.isArray(testcase.error) ? testcase.error : [testcase.error];
                            testError.forEach(function(error) {
                                if (error) {
                                    console.log(error.$.message)
                                    note = error.$.message;
                                    classStatus = 'failed';
                                }
                            });

                            var testSkipped = Array.isArray(testcase.skipped) ? testcase.skipped : [testcase.skipped];
                            testSkipped.forEach(function(skipped) {
                                if (skipped) {
                                    classStatus = 'skipped';
                                }
                            });

                            console.log(classStatus);

                            var testLog = {
                                status: classStatus,
                                name: className,
                                attachments: [],
                                note: note,
                                exe_start_date: startTime,
                                exe_end_date: endTime,
                                automation_content: htmlEntities(className),
                                module_names: moduleNames
                            };
                            if (stack !== '') {
                            testLog.attachments.push({
                                name: `${className}.txt`,
                                data: Buffer.from(stack).toString("base64"),
                                content_type: "text/plain"
                            });
                            }
                            //testLog.attachments.push(payload.consoleOutput[0]);
                            testLogs.push(testLog);
